	public void run() throws Exception {
		for (VLFPTestCase td : VLFP_TESTS) {
			ByteArrayInputStream bin = new ByteArrayInputStream(td.vlfpbe);
			VLFPItem item = new VLFPItem(0, "", "", false, true);
			VLFPItem.Instance inst = item.read(new Closure(null), new BufferedCountedInputStream(bin));
			Number actual = inst.getValue();
			Number expected = td.value;
			expectEquals(actual, expected);
			
			bin = new ByteArrayInputStream(td.vlfple);
			item = new VLFPItem(0, "", "", true, true);
			inst = item.read(new Closure(null), new BufferedCountedInputStream(bin));
			actual = inst.getValue();
			expected = td.value;
			expectEquals(actual, expected);
		}
	}
	
